sys_script_include.script:
var needit_variables_lookup_si = Class.create();
needit_variables_lookup_si.prototype = {
    initialize: function() {
    },

	find_variables: function(item) {
		var filter = 'sys_idIN=';
		var variableSets = '';

		//grab the variable sets
		var grVS = new GlideRecord("io_set_item");
		grVS.addQuery("sc_cat_item", item);
		grVS.query();
		while (grVS.next()) {
			variableSets += grVS.variable_set+",";
		}

		//Grab all the variables that set on the form
		var grVar = new GlideRecord("item_option_new");
		grVar.addQuery("cat_item", item).addOrCondition("variable_set", "IN", variableSets);
		grVar.query();
		while (grVar.next()) {
			filter += grVar.sys_id+",";
		}
		return filter;
	},

    type: 'needit_variables_lookup_si'
};
